    def m_rest_fun(self):
        self.lineEdit_11.setText("")  
        self.lineEdit_13.setText("") 
        self.lineEdit_12.setText("")
        #self.lineEdit_14.setText("")  
        self.pushButton_8.setEnabled(True) #add
        self.pushButton_9.setDisabled(True)  #save
        self.pushButton_10.setDisabled(True) #delete           
        self.pushButton_11.setEnabled(True) #reset
        self.label_54.hide()
        self.label_55.hide()
       
        self.m_select_all_data()
        self.label_49.setText("00001")
  
        connection = sqlite3.connect("fci.db")
        results=connection.execute("select seq+1 from sqlite_sequence WHERE name = 'RATES_CONF'")       
        for x in results:           
                 self.label_49.setText(str(x[0]).zfill(6))
            
        connection.close() 
    
    def m_fetch_data_from_tw(self):        
        row = self.tableWidget.currentRow()     
        if(row != -1 ):
            self.dr_id=str(self.tableWidget.item(row, 0).text())
            self.label_49.setText(str(self.tableWidget.item(row, 0).text())) 
            self.lineEdit_13.setText(str(self.tableWidget.item(row, 1).text())) #
            self.lineEdit_11.setText(str(self.tableWidget.item(row, 2).text()))  #
            self.lineEdit_12.setText(str(self.tableWidget.item(row, 3).text()))  #
            
            self.pushButton_8.setDisabled(True) #add
            self.pushButton_9.setEnabled(True)  #save
            self.pushButton_10.setEnabled(True) #delete           
            self.pushButton_11.setEnabled(True) #reset
            
        else:    
            self.label_54.setText("Please Select the record.")
            self.label_54.show()
  
    def m_load_data(self):        
        if(self.operation_flg=="ADD"):
                #print("inside Add ")
                self.m_add_data()
        elif(self.operation_flg=="EDIT"):
                #print("inside edit ")
                self.m_edit_data()
        elif(self.operation_flg=="DELETE"):
                #print("inside delete ")
                self.m_delete_data()
        else:
                print("Invalid Operation.")
         
    def m_add_click(self):
        self.operation_flg="ADD"       
        self.m_load_data()
        
    def m_add_data(self):
        if(self.lineEdit_13.text() != ""):            
            connection = sqlite3.connect("fci.db")
            with connection:        
                    cursor = connection.cursor()
                    cursor.execute("INSERT INTO RATES_CONF(VEHICLE_TYPE,RATE,MAX_LOAD) VALUES('"+self.lineEdit_13.text()+"','"+self.lineEdit_11.text()+"','"+self.lineEdit_12.text()+"')")                    
            connection.commit();                    
            connection.close()  
      
            self.label_54.setText("Record Added Successfully.")
            self.log_audit("MASTER DATA","Added VEHICLE_TYPE :" +str(self.lineEdit_13.text()))
            self.label_54.show()
        else :
            self.label_54.setText("VEHICLE_TYPE is Empty.")
            self.label_54.show()
            
        self.m_select_all_data()
    
    def m_edit_click(self):
        row = self.tableWidget.currentRow()     
        if(row != -1 ):
            self.operation_flg="EDIT"
            self.m_load_data()
        else:    
            self.label_54.setText("Please Select the record.")
            self.label_54.show() 
    
    def m_edit_data(self):
        if(self.lineEdit_13.text() != ""):
            connection = sqlite3.connect("fci.db")
            with connection:        
                    cursor = connection.cursor()
                    cursor.execute("UPDATE RATES_CONF SET VEHICLE_TYPE='"+self.lineEdit_13.text()+"',RATE='"+self.lineEdit_11.text()+"',MAX_LOAD='"+self.lineEdit_12.text()+"' WHERE  RATE_ID ='"+str(self.dr_id)+"'")                    
            connection.commit();                    
            connection.close()   
       
        self.label_54.setText("Record Saved Successfully.")
        self.log_audit("MASTER DATA","Updated Material Id :" +str(self.lineEdit_12.text()))
        self.label_54.show()
        self.m_select_all_data()
    
    def m_delete_click(self):
        row = self.tableWidget.currentRow()     
        if(row != -1 ):
            self.operation_flg="DELETE"
            self.m_load_data()
        else:    
            self.label_54.setText("Please Select the record.")
            self.label_54.show()        
     
      
    def m_delete_data(self):
        if(self.lineEdit_13.text() != ""):
            connection = sqlite3.connect("fci.db")
            with connection:        
                    cursor = connection.cursor()
                    cursor.execute("DELETE FROM RATES_CONF WHERE RATE_ID ='"+str(self.dr_id)+"'")                    
            connection.commit();                    
            connection.close()
            
            self.label_54.setText("Record Deleted Successfully.")
            self.log_audit("MASTER DATA","Deleted Material Id :" +str(self.lineEdit_12.text()))
            
            self.label_54.show()
            
            self.m_select_all_data()
    
   
    
    
    def m_select_all_data(self):     
        self.m_delete_all_records()        
        font = QtGui.QFont()
        font.setPointSize(10)
        self.tableWidget.setFont(font) 
        self.tableWidget.horizontalHeader().setStretchLastSection(True)
        self.tableWidget.setColumnWidth(0, 200)
        self.tableWidget.setColumnWidth(1, 200)
        self.tableWidget.setColumnWidth(2, 200)
        self.tableWidget.setColumnWidth(3, 200)
        self.tableWidget.setHorizontalHeaderLabels(['RATE ID', ' VEHICLE TYPE ', 'RATE','MAX.LOAD'])
        connection = sqlite3.connect("fci.db")
        results=connection.execute("select RATE_ID,VEHICLE_TYPE,RATE,MAX_LOAD FROM RATES_CONF")                        
        for row_number, row_data in enumerate(results):            
            self.tableWidget.insertRow(row_number)
            for column_number, data in enumerate(row_data):
                self.tableWidget.setItem(row_number,column_number,QTableWidgetItem(str (data)))                
        connection.close()   
        #self.tableWidget.resizeColumnsToContents()
        self.tableWidget.resizeRowsToContents()
        self.tableWidget.horizontalHeader().setStretchLastSection(True)
        self.tableWidget.setEditTriggers(QtWidgets.QTableWidget.NoEditTriggers)
        
    
    def m_delete_all_records(self):
        i = self.tableWidget.rowCount()       
        while (i>0):             
            i=i-1
            self.tableWidget.removeRow(i)
            
    
    def load_parties_list(self):
        self.listWidget.clear()
        connection = sqlite3.connect("fci.db")
