    def fetch_via_file_list(self):
        v_str=str(self.listWidget.currentItem().text())
        #self.re_str = str(v_str)                
        self.lineEdit_14.setText(str(v_str))
        self.lineEdit_15.setText(str(v_str))
        
    def save_data(self):
        if(self.lineEdit_14.text() == ""):
             self.label_55.setText("Please select PARTY NAME.")           
             self.label_55.show()
        elif(self.lineEdit_15.text() ==""):
             self.label_55.setText("NEW PARTY NAME is EMPTY.")           
             self.label_55.show()
        else:    
                connection = sqlite3.connect("fci.db")
                with connection:        
                    cursor = connection.cursor()
                    cursor.execute("UPDATE WEIGHT_MST SET PARTY_NAME='"+self.lineEdit_15.text()+"'   WHERE  PARTY_NAME = '"+self.lineEdit_14.text()+"'")
                    
                connection.commit();
                connection.close()
                self.label_55.setText("UPDATED PARY NAME SUCCESSFULLY.")
                self.log_audit("MASTER DATA","PARTY_NAME as :" +str(self.lineEdit_15.text()))
                self.label_55.show()
                self.load_parties_list()


    def log_audit(self,event_name,desc_str):        
        connection = sqlite3.connect("fci.db")
        with connection:        
            cursor = connection.cursor()       
            cursor.execute("INSERT INTO AUDIT_MST(AUDIT_TYPE,MESSAGE) VALUES(?,?)",(event_name,desc_str))
            cursor.execute("UPDATE AUDIT_MST SET USER_ID = (SELECT LOGIN_USER_ID FROM GLOBAL_VAR) WHERE USER_ID IS NULL")            
        connection.commit();
        connection.close()

