    def sendSMS(self,apikey, numbers, sender, message):
        if(numbers != ""):            
            fr ='None'
            connection = sqlite3.connect("fci.db")       
            results=connection.execute("SELECT SMS_ACTIVATION,SMS_API_KEY,SMS_API_URL FROM GLOBAL_VAR")
            for x in results:
                if(str(x[0]) == "ACTIVE"):
                    apikey=str(x[1])
                    try:
                        data =  urllib.parse.urlencode({'apikey': apikey, 'numbers': numbers,'message' : message, 'sender': sender})
                        data = data.encode('utf-8')
                        #request = urllib.request.Request("https://api.textlocal.in/send/?")
                        request = urllib.request.Request(str(x[2]))
                        f = urllib.request.urlopen(request, data)
                        fr = f.read()
                    except IOError:
                        print("IO Errors") 
                    return(fr)
                
                else:
                    print(" SMS Deactivated")
                    return("ERROR")
            connection.close()  
                
                
        else:
            print("SendSMS : Invalid Number.")
            return("ERROR")
        
