    def on_button_clicked(self):
        for button in (self.buttons()):
             button.clicked.connect(lambda _, value=str(button.text()): self.set_value_to_lineEdit(self.line_edit, value))  
    
    def set_value_to_lineEdit(self, lineEdit, value):
        # print("button text value :", value)
        
        if lineEdit is None:
             print("Invalid Line Edit")
             return
        if (value == "."):
                if lineEdit.text().find(".") == -1 :
                        cursor_position = lineEdit.cursorPosition() 
                        # print(cursor_position, "cursor")
                        lineEdit.setText(str(lineEdit.text()[:cursor_position]) + str(value) + str(lineEdit.text()[cursor_position:])) 
                        lineEdit.setCursorPosition(cursor_position + 1) 
                else:
                        print("Already '.' pressed")        

        else:
                cursor_position = lineEdit.cursorPosition() 
                # print(cursor_position, "cursor")
                lineEdit.setText(str(lineEdit.text()[:cursor_position]) + str(value) + str(lineEdit.text()[cursor_position:])) 
                lineEdit.setCursorPosition(cursor_position + 1)   
             
    
    def delete_character(self, lineEdit):
        
        cursor_position = lineEdit.cursorPosition() 
        if lineEdit is None:
             print("Invalid line edit type")
             return
        if cursor_position != 0:
                lineEdit.setText(lineEdit.text()[:cursor_position - 1] + lineEdit.text()[cursor_position:])
                lineEdit.setCursorPosition(cursor_position - 1)
        else:
                print("Cursor index is 0 can not delete the text please enter the text to delete")
    def buttons(self):
        # Return the list of buttons for assigning the letters
        return [
            self.pushButton_2, self.pushButton_3, self.pushButton_5, self.pushButton_6,
            self.pushButton_7, self.pushButton_8, self.pushButton_9, self.pushButton_10,
            self.pushButton_11, self.pushButton_4, self.pushButton_21
        ]
    
